import React from 'react';
import "./Eightball.css"
import { useState } from "react";


const Eightball = ({props}) => {
    console.log('i am props', props)
    const genRandom = () => Math.floor(Math.random() * props.length);
    // const currResponse = () => {

    // }
    // const [msg, setMsg ] = useState({props[genRandom()].msg})
    // const [color, setColor ] = useState
    return (
        <div className="Eightball">
            <h3>Magic Eightball message:{props[genRandom()].color}</h3>
            <h3>Magic Eightball color: {props[genRandom()].color}</h3>
        </div>
    );
};

export default Eightball;
