import React from 'react';
import "./Eightball.css"
import { useState } from "react";
import FadeIn from 'react-fade-in/lib/FadeIn';


const Eightball = ({props}) => {
    const [currColor, setCurrColor] = useState("black")
    const [currMsg, setCurrMsg] = useState("Think of a Question")
    const genRandomIdx = () => Math.floor(Math.random() * props.length);

    const handleClick = () => {
        const getRandomIdx = genRandomIdx()
        setCurrMsg(props[getRandomIdx].msg)
        setCurrColor(props[getRandomIdx].color)
        setFade(`animation: 3s ease-in 3s slidein`)
    }

    return (
        <div className = "Eightball" >
            <div onClick={handleClick} className="Eightball-circle" style={{backgroundColor: currColor, fade} }>
                <p className="Eightball-msg" style={{fade}}>{currMsg} </p>
                {/* {currMsg} */}
            </div>
        </div>
    );
};

export default Eightball;


