import "./Eightball.css"
import { useState } from "react";
import answers from './answers';


const Eightball = ({msg, color}) => {
    return (
        <div className="Eightball">
            <h3>Magic Eightball {msg} {color}</h3>
        </div>
    );
};

export default Eightball;
